import { useState, useContext, useRef } from "react";
import styles from "./ConfirmationForm.module.css";
import Button from "../UI/Button";
import { CartContext } from "../../storage/CartContext";
const isEmpty = (value) => {
  return value.trim() === "";
};
const ConfirmationForm = () => {
  const nameRef = useRef("");

  const phoneRef = useRef("");

  const postalRef = useRef("");

  const addressRef = useRef("");

  const [formInputsValidity, setFormInputsValidity] = useState({
    nameIsValid: true,
    phoneIsValid: true,
    postalIsValid: true,
    addressIsValid: true,
  });
  const ctx = useContext(CartContext);

  const formSubmitHandler = (event) => {
    event.preventDefault();
    const nameIsValid = !isEmpty(nameRef.current.value);
    const phoneIsValid = !isEmpty(phoneRef.current.value);
    const addressIsValid = !isEmpty(addressRef.current.value);
    const postalIsValid = !isEmpty(postalRef.current.value);

    setFormInputsValidity({
      nameIsValid: nameIsValid,
      phoneIsValid: phoneIsValid,
      postalIsValid: postalIsValid,
      addressIsValid: addressIsValid,
    });

    const formIsValid =
      phoneIsValid && nameIsValid && addressIsValid && postalIsValid;
    if (!formIsValid) return;
  };

  return (
    <form onSubmit={formSubmitHandler}>
      <div className={styles["form-control"]}>
        <label htmlFor="name">Name</label>
        <input ref={nameRef} type="text" id="name"></input>
        {!formInputsValidity.nameIsValid && <p>please enter a valid name</p>}
      </div>
      <div className={styles["form-control"]}>
        <label htmlFor="phone number">Phone Number</label>
        <input ref={phoneRef} type="text" id="phone number"></input>
        {!formInputsValidity.phoneIsValid && (
          <p>please enter a valid Phone Number</p>
        )}
      </div>
      <div className={styles["form-control"]}>
        <label htmlFor="postal">Postal Code</label>
        <input ref={postalRef} type="text" id="postal"></input>
        {!formInputsValidity.postalIsValid && (
          <p>please enter a valid Postal Code</p>
        )}
      </div>
      <div className={styles["form-control"]}>
        <label htmlFor="location">Address</label>
        <input ref={addressRef} type="text" id="location"></input>
        {!formInputsValidity.addressIsValid && (
          <p>please enter a valid address</p>
        )}
      </div>
      <Button onClick={ctx.cartExpandHandler}>Cancel</Button>
      <Button type="submit">Confirm</Button>
    </form>
  );
};

export default ConfirmationForm;
