import {useState, useRef, useContext } from "react";
import styles from "./ConfirmationForm.module.css";
import Button from "../UI/Button";
import { CartContext } from "../../storage/CartContext";
const checkValidity = (ref) => {
    return ref.current.value.trim() === "";
  };
const ConfirmationForm = () => {
  const nameRef = useRef('');
  const phoneRef = useRef('');
  const postalRef = useRef('');
  const locationRef = useRef('');
  const ctx = useContext(CartContext);
  const [formIsValid,setFormIsValid]=useState(true)

  const formIsNotValid =
    checkValidity(nameRef) ||
    checkValidity(phoneRef) ||
    checkValidity(postalRef) ||
    checkValidity(locationRef);

  const formSubmitHandler = (event) => {
    event.preventDefault();
  };
  return (
    <form onSubmit={formSubmitHandler}>
      <div className={styles["form-control"]}>
        <label htmlFor="name">Name</label>
        <input ref={nameRef} type="text" id="name"></input>
      </div>
      <div className={styles["form-control"]}>
        <label htmlFor="phone number">Phone Number</label>
        <input ref={phoneRef} type="text" id="phone number"></input>
      </div>
      <div className={styles["form-control"]}>
        <label htmlFor="postal">Postal Code</label>
        <input ref={postalRef} type="text" id="postal"></input>
      </div>
      <div className={styles["form-control"]}>
        <label htmlFor="location">Location</label>
        <input ref={locationRef} type="text" id="location"></input>
      </div>
      <Button onClick={ctx.cartExpandHandler}>Cancel</Button>
      <Button disabled={formIsNotValid}>Confirm</Button>
    </form>
  );
};

export default ConfirmationForm;
