import React from "react";
import { useState } from "react";
const CartContext = React.createContext({
  cartExpanded: false,
  cartHandler: () => {},
  cartItemsAdder: (data) => {},
  cartItems: [{ name: "", description: "", price: "", id: "", amount: "" }],
});

const CartProvider = (props) => {
  const [cartExpanded, setCartExpanded] = useState(false);
  const [cartItems, setCartItems] = useState([]);
  const cartExpandHandler = () => {
    setCartExpanded((previousState) => !previousState);
  };
  const cartItemsAdder = (data, action) => {
    const existingCartItemIndex = cartItems.findIndex(
      (item) => item.name === data.name
    );

    if (existingCartItemIndex !== -1) {
      // Item already exists in the cart, update the amount
      cartItems[existingCartItemIndex].amount = data.amount;
      setCartItems(cartItems);
    } else {
      // Item doesn't exist in the cart, add it
      setCartItems((previousCartItems) => [...previousCartItems, data]);
    }
    //handling add button in the cart items
    if (action === 'add') {
      // Increment the amount
      cartItems[existingCartItemIndex].amount += 1;
    } else if (action === 'minus') {
      // Decrement the amount, and remove the item if the amount reaches 0
      cartItems[existingCartItemIndex].amount -= 1;
      if ( cartItems[existingCartItemIndex].amount === 0) {
        updatedCartItems.splice(existingCartItemIndex, 1);
      }
  };
}
  return (
    <CartContext.Provider
      value={{
        cartExpanded: cartExpanded,
        cartExpandHandler: cartExpandHandler,
        cartItems: cartItems,
        cartItemsAdder: cartItemsAdder,
      }}
    >
      {props.children}
    </CartContext.Provider>
  );
};
export { CartContext };
export default CartProvider;
