import styles from "./MealItem.module.css";
import { useState } from "react";
import Button from "../UI/Button";
import { useContext } from "react";
import { CartContext } from "../../storage/CartContext";
const MealItem = (props) => {
  const [amount, setAmount] = useState(0);
  const ctx = useContext(CartContext);
  const addHandler = () => {
    setAmount((prevAmount) => prevAmount + 1);

    const existingCartItem = ctx.cartItems.find(
      (item) => item.name === props.name
    );

    if (existingCartItem) {
      // Item already exists in the cart, increment the amount
      const updatedAmount = existingCartItem.amount + 1;

      ctx.cartItemsAdder({
        amount: updatedAmount,
      });
    } else {
      // Item doesn't exist in the cart, add it with an initial amount of 1
      ctx.cartItemsAdder({
        name: props.name,
        description: props.description,
        price: props.price,
        id: Math.random(),
        amount: 1,
      });
    }
  };

  return (
    <li className={styles["meal-item"]}>
      <div className={styles.info}>
        <p className={styles.name}>{props.name}</p>
        <span className={styles.description}>{props.description}</span>
        <span className={styles.price}>
          <br />${props.price}
        </span>
      </div>
      <div className={styles["add-amount"]}>
        <p>Amount {amount}</p>
        <Button type="button" onClick={addHandler}>
          + Add
        </Button>
      </div>
    </li>
  );
};

export default MealItem;
