import { useState, useContext } from "react";
import styles from "./ConfirmationForm.module.css";
import Button from "../UI/Button";
import { CartContext } from "../../storage/CartContext";

const ConfirmationForm = () => {
  const [name, setName] = useState('');
  const [phone, setPhone] = useState('');
  const [postal, setPostal] = useState('');
  const [location, setLocation] = useState('');
  const ctx = useContext(CartContext);

  const checkValidity = (value) => {
    return value.trim() == "";
  };
  const formIsValid =
    checkValidity(name) ||
    checkValidity(phone) ||
    checkValidity(postal) ||
    checkValidity(location)
      ? false
      : true;

  const formSubmitHandler = (event) => {
    event.preventDefault();

    if (!formIsValid) {
      setName("");
      setPhone("");
      setLocation("");
      setPostal("");
      return;
    }

    console.log(name);
  };
  return (
    <form onSubmit={formSubmitHandler}>
      <div className={styles["form-control"]}>
        <label htmlFor="name">Name</label>
        <input value={name} type="text" id="name"></input>
      </div>
      <div className={styles["form-control"]}>
        <label htmlFor="phone number">Phone Number</label>
        <input value={phone} type="text" id="phone number"></input>
      </div>
      <div className={styles["form-control"]}>
        <label htmlFor="postal">Postal Code</label>
        <input value={postal} type="text" id="postal"></input>
      </div>
      <div className={styles["form-control"]}>
        <label htmlFor="location">Location</label>
        <input value={location} type="text" id="location"></input>
      </div>
      <Button onClick={ctx.cartExpandHandler}>Cancel</Button>
      <Button type="submit" disabled={formIsValid} onClick={formSubmitHandler}>
        Confirm
      </Button>
    </form>
  );
};

export default ConfirmationForm;
