import { useState, useContext, useRef } from "react";
import styles from "./ConfirmationForm.module.css";
import Button from "../UI/Button";
import { CartContext } from "../../storage/CartContext";
const isEmpty = (value) => {
  return value.trim() === "";
};
const ConfirmationForm = () => {
    const [formInputsValidity, setFormInputsValidity] = useState({
        name: true,
        street: true,
        city: true,
        postalCode: true,
      });
    
      const nameInputRef = useRef();
      const phoneInputRef = useRef();
      const postalCodeInputRef = useRef();
      const cityInputRef = useRef();
    
      const confirmHandler = (event) => {
        event.preventDefault();
    
        const enteredName = nameInputRef.current.value;
        const enteredStreet = phoneInputRef.current.value;
        const enteredPostalCode = postalCodeInputRef.current.value;
        const enteredCity = cityInputRef.current.value;
    
        const enteredNameIsValid = !isEmpty(enteredName);
        const enteredStreetIsValid = !isEmpty(enteredStreet);
        const enteredCityIsValid = !isEmpty(enteredCity);
        const enteredPostalCodeIsValid = isEmpty(enteredPostalCode);
    
        setFormInputsValidity({
          name: enteredNameIsValid,
          street: enteredStreetIsValid,
          city: enteredCityIsValid,
          postalCode: enteredPostalCodeIsValid,
        });
    
        const formIsValid =
          enteredNameIsValid &&
          enteredStreetIsValid &&
          enteredCityIsValid &&
          enteredPostalCodeIsValid;
    
        if (!formIsValid) {
          return;
        }
    
        // Submit cart data
      };
  const ctx = useContext(CartContext);

  

  return (
    <form onSubmit={confirmHandler}>
      <div className={styles["form-control"]}>
        <label htmlFor="name">Name</label>
        <input ref={nameInputRef} type="text" id="name"></input>
        {!formInputsValidity.name && <p>please enter a valid name</p>}
      </div>
      <div className={styles["form-control"]}>
        <label htmlFor="phone number">Phone Number</label>
        <input ref={phoneInputRef} type="text" id="phone number"></input>
        {!formInputsValidity.phone && <p>please enter a valid Phone Number</p>}
      </div>
      <div className={styles["form-control"]}>
        <label htmlFor="postal">Postal Code</label>
        <input ref={postalCodeInputRef} type="text" id="postal"></input>
        {!formInputsValidity.postal && <p>please enter a valid Postal Code</p>}
      </div>
      <div className={styles["form-control"]}>
        <label htmlFor="location">Address</label>
        <input ref={cityInputRef} type="text" id=""></input>
        {!formInputsValidity.address && <p>please enter a valid address</p>}
      </div>
      <Button onClick={ctx.cartExpandHandler}>Cancel</Button>
      <Button type="submit">Confirm</Button>
    </form>
  );
};

export default ConfirmationForm;
